INSERT INTO items (slug, name, description, content, category, city, state, latitude, longitude, source_url) VALUES
('signal-coffee', 'Signal Coffee', 'Minimalist cafe with lightning-fast fiber internet and standing desks.', 'Signal Coffee is a dream for remote workers who need reliable, blazing-fast internet. Located in downtown Austin, this minimalist space features standing desks, noise-canceling pods, and a curated menu of single-origin pour-overs. The fiber connection consistently tests at 500+ Mbps.', 'Fast WiFi', 'Austin', 'TX', 30.2672, -97.7431, 'https://example.com/signal-coffee'),

